<?phpclass KanbaneryPlugin extends MantisPlugin {    function register() {        $this->name        = 'Kanbanery';    $this->description = 'Extension to submit Mantis task to Kanbanery';    $this->page        = 'config';    # Proper name of plugin    # Short description of the plugin    # Default plugin page    # Plugin version string    $this->version = '1.0';    $this->requires = array(        # Plugin dependencies        'MantisCore' => '1.2.0'    );    $this->author  = 'Tomas Dermisek';    $this->contact = '';    $this->url     = 'https://github.com/dermo666/MantisToKanbanery';    # Author/team name    # Author/team e-mail address    # Support webpage  }    /**   * Default plugin configuration.   */  function config() {    return array(        'kanbanery_api_key'    => '',        'kanbanery_workspace'  => '',        'kanbanery_project_id' => '', // If this is specified Board selection won't be displayed.        'kanbanery_task_type'	 => 'Office Work',    );  }  }/** * Print a button to move the given bug to Kanbanery. *  * @param int $p_bug_id *  * @return null */function custom_function_override_print_bug_view_page_custom_buttons( $p_bug_id ) {  //if( access_has_bug_level( config_get( 'move_bug_threshold' ), $p_bug_id ) ) {  //}  echo '<td class="center">';  echo '<form method="post" action="'.plugin_page( 'move', FALSE, 'Kanbanery' ).'">';  echo form_security_field( 'move_to_kanbanery' );  echo "	<input type=\"hidden\" name=\"bug_id\" value=\"$p_bug_id\" />\n";  $t_button_text = plugin_lang_get( 'move_to_kanbanery', 'Kanbanery');  echo "  <input type=\"submit\" class=\"button\" value=\"$t_button_text\" />";  echo "</form>\n";  echo '</td>';}